export interface ICatCard {
  /**
   * Title of the Card
   */
  title: string;
  /**
   * Tags to associate the card with
   */
  tag: string;
  /**
   * Text to display in Card body
   */
  body: string;
  /**
   * Name of content maker
   */
  author: string;
  /**
   * The time the article was published
   */
  time: string;
}
